import React, { useState, useEffect } from "react";
import { useSocket } from "./socket/socket.js";

function App() {
  const [username, setUsername] = useState("");
  const [message, setMessage] = useState("");
  const [hasJoined, setHasJoined] = useState(false);
  const {
    isConnected,
    messages,
    users,
    typingUsers,
    connect,
    sendMessage,
    setTyping,
    socket
  } = useSocket();

  // Test socket connection
  useEffect(() => {
    console.log("Socket ID:", socket?.id);
    console.log("Socket connected:", socket?.connected);
  }, [socket]);

  const handleJoin = (e) => {
    e.preventDefault();
    console.log("Join button clicked. Username:", username);
    console.log("Socket available:", !!socket);
    console.log("Socket connected:", socket?.connected);

    if (username.trim()) {
      console.log("Calling connect with username:", username);
      connect(username);
      setHasJoined(true);
      console.log("hasJoined set to true");
    } else {
      console.log("Username is empty");
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && isConnected && hasJoined) {
      console.log("Sending message:", message);
      sendMessage({ username, message });
      setMessage("");
      setTyping(false);
    }
  };

  console.log("App rendering - hasJoined:", hasJoined, "isConnected:", isConnected, "username:", username);

  // Show join form if not joined yet, even if socket is connected
  if (!hasJoined) {
    return (
      <div style={{ padding: "20px" }}>
        <h1>üí¨ Chat Application</h1>
        <p>Status: {isConnected ? "‚úÖ Connected to server" : "üîÑ Connecting..."}</p>
        <form onSubmit={handleJoin}>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ padding: "8px", marginRight: "10px" }}
          />
          <button type="submit" style={{ padding: "8px 16px" }}>
            Join Chat
          </button>
        </form>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>üí¨ Chat Room</h1>
      <div style={{ marginBottom: "20px" }}>
        <strong>Status:</strong> {isConnected ? "‚úÖ Connected" : "‚ùå Disconnected"} as {username}
      </div>

      <div style={{ display: "flex", gap: "20px" }}>
        <div style={{ flex: 2 }}>
          <h3>Messages</h3>
          <div style={{ border: "1px solid #ccc", height: "300px", overflowY: "scroll", padding: "10px" }}>
            {messages.length === 0 ? (
              <div style={{ color: "#666", fontStyle: "italic" }}>No messages yet. Send a message!</div>
            ) : (
              messages.map((msg, index) => (
                <div key={index} style={{ marginBottom: "10px" }}>
                  {msg.system ? (
                    <em style={{ color: "#666" }}>{msg.message}</em>
                  ) : (
                    <div>
                      <strong>{msg.username}:</strong> {msg.message}
                    </div>
                  )}
                </div>
              ))
            )}
          </div>

          <form onSubmit={handleSendMessage} style={{ marginTop: "10px" }}>
            <input
              type="text"
              placeholder="Type a message..."
              value={message}
              onChange={(e) => {
                setMessage(e.target.value);
                setTyping(true);
              }}
              style={{ padding: "8px", width: "70%", marginRight: "10px" }}
            />
            <button type="submit" style={{ padding: "8px 16px" }}>
              Send
            </button>
          </form>

          {typingUsers.length > 0 && (
            <div style={{ color: "#666", fontStyle: "italic", marginTop: "5px" }}>
              {typingUsers.join(", ")} {typingUsers.length === 1 ? "is" : "are"} typing...
            </div>
          )}
        </div>

        <div style={{ flex: 1 }}>
          <h3>Online Users ({users.length})</h3>
          <div style={{ border: "1px solid #ccc", padding: "10px" }}>
            {users.length > 0 ? (
              users.map((user, index) => (
                <div key={index}>üë§ {user.username}</div>
              ))
            ) : (
              <div style={{ color: "#666", fontStyle: "italic" }}>No users online</div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
