import React, { useState, useEffect } from "react";
import { useSocket } from "./socket/socket.js";

function App() {
  const [username, setUsername] = useState("");
  const [message, setMessage] = useState("");
  const [privateMessage, setPrivateMessage] = useState("");
  const [selectedUser, setSelectedUser] = useState("");
  const [hasJoined, setHasJoined] = useState(false);
  const [activeTab, setActiveTab] = useState("public"); // "public" or "private"
  const {
    isConnected,
    messages,
    privateMessages,
    users,
    typingUsers,
    connect,
    sendMessage,
    sendPrivateMessage,
    setTyping,
    socket
  } = useSocket();

  const handleJoin = (e) => {
    e.preventDefault();
    if (username.trim()) {
      connect(username);
      setHasJoined(true);
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && hasJoined) {
      sendMessage({ username, message });
      setMessage("");
      setTyping(false);
    }
  };

  const handleSendPrivateMessage = (e) => {
    e.preventDefault();
    if (privateMessage.trim() && selectedUser && hasJoined) {
      sendPrivateMessage(selectedUser, privateMessage, username);
      setPrivateMessage("");
    }
  };

  const handleUserSelect = (user) => {
    setSelectedUser(user.username);
    setActiveTab("private");
  };

  // Filter private messages for the current conversation
  const currentPrivateMessages = privateMessages.filter(
    msg => 
      (msg.from === username && msg.to === selectedUser) ||
      (msg.from === selectedUser && msg.to === username)
  );

  console.log("App rendering - hasJoined:", hasJoined, "activeTab:", activeTab, "selectedUser:", selectedUser);

  // Show join form if not joined yet
  if (!hasJoined) {
    return (
      <div style={{ padding: "20px" }}>
        <h1>üí¨ Chat Application</h1>
        <p>Status: {socket?.connected ? "‚úÖ Connected to server" : "üîÑ Connecting..."}</p>
        <form onSubmit={handleJoin}>
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{ padding: "8px", marginRight: "10px", border: "2px solid #000" }}
          />
          <button type="submit" style={{ padding: "8px 16px" }}>
            Join Chat
          </button>
        </form>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>üí¨ Chat Room</h1>
      <div style={{ marginBottom: "20px" }}>
        <strong>Status:</strong> {socket?.connected ? "‚úÖ Connected" : "‚ùå Disconnected"} as {username}
      </div>

      <div style={{ display: "flex", gap: "20px" }}>
        {/* Online Users Section */}
        <div style={{ flex: 1 }}>
          <h3>Online Users ({users.length})</h3>
          <div style={{ border: "1px solid #ccc", padding: "10px", maxHeight: "400px", overflowY: "scroll" }}>
            {users.length > 0 ? (
              users
                .filter(user => user.username !== username) // Don't show yourself
                .map((user, index) => (
                  <div 
                    key={index} 
                    style={{ 
                      padding: "8px", 
                      margin: "5px 0", 
                      backgroundColor: selectedUser === user.username ? "#e3f2fd" : "transparent",
                      border: selectedUser === user.username ? "2px solid #2196f3" : "1px solid #ddd",
                      borderRadius: "4px",
                      cursor: "pointer"
                    }}
                    onClick={() => handleUserSelect(user)}
                  >
                    üë§ {user.username}
                    {selectedUser === user.username && " üí¨"}
                  </div>
                ))
            ) : (
              <div style={{ color: "#666", fontStyle: "italic" }}>No other users online</div>
            )}
          </div>
        </div>

        {/* Chat Section */}
        <div style={{ flex: 2 }}>
          {/* Tab Navigation */}
          <div style={{ marginBottom: "10px", borderBottom: "1px solid #ccc" }}>
            <button
              onClick={() => setActiveTab("public")}
              style={{
                padding: "8px 16px",
                backgroundColor: activeTab === "public" ? "#2196f3" : "transparent",
                color: activeTab === "public" ? "white" : "black",
                border: "none",
                borderRadius: "4px 4px 0 0",
                marginRight: "5px"
              }}
            >
              Public Chat
            </button>
            <button
              onClick={() => setActiveTab("private")}
              disabled={!selectedUser}
              style={{
                padding: "8px 16px",
                backgroundColor: activeTab === "private" ? "#2196f3" : "transparent",
                color: activeTab === "private" ? "white" : selectedUser ? "black" : "#999",
                border: "none",
                borderRadius: "4px 4px 0 0"
              }}
            >
              Private {selectedUser && `with ${selectedUser}`}
            </button>
          </div>

          {/* Messages Display */}
          <div style={{ border: "1px solid #ccc", height: "300px", overflowY: "scroll", padding: "10px" }}>
            {activeTab === "public" ? (
              messages.length === 0 ? (
                <div style={{ color: "#666", fontStyle: "italic" }}>No public messages yet. Send a message!</div>
              ) : (
                messages.map((msg, index) => (
                  <div key={index} style={{ marginBottom: "10px" }}>
                    {msg.system ? (
                      <em style={{ color: "#666" }}>{msg.message}</em>
                    ) : msg.type === 'private' ? (
                      <div style={{ backgroundColor: "#fff3cd", padding: "5px", borderRadius: "4px" }}>
                        <strong>üîí {msg.from} ‚Üí {msg.to}:</strong> {msg.message}
                      </div>
                    ) : (
                      <div>
                        <strong>{msg.username}:</strong> {msg.message}
                      </div>
                    )}
                  </div>
                ))
              )
            ) : (
              // Private Messages
              currentPrivateMessages.length === 0 ? (
                <div style={{ color: "#666", fontStyle: "italic" }}>
                  {selectedUser ? `No private messages with ${selectedUser} yet. Start a conversation!` : "Select a user to start private chat"}
                </div>
              ) : (
                currentPrivateMessages.map((msg, index) => (
                  <div key={index} style={{ marginBottom: "10px" }}>
                    <div style={{ 
                      backgroundColor: msg.from === username ? "#d4edda" : "#f8d7da",
                      padding: "8px",
                      borderRadius: "8px",
                      marginLeft: msg.from === username ? "50px" : "0",
                      marginRight: msg.from === username ? "0" : "50px"
                    }}>
                      <div>
                        <strong>{msg.from === username ? "You" : msg.from}:</strong> {msg.message}
                      </div>
                      <div style={{ fontSize: "0.8em", color: "#666", marginTop: "4px" }}>
                        {new Date(msg.timestamp).toLocaleTimeString()}
                      </div>
                    </div>
                  </div>
                ))
              )
            )}
          </div>

          {/* Message Input */}
          {activeTab === "public" ? (
            <form onSubmit={handleSendMessage} style={{ marginTop: "10px" }}>
              <input
                type="text"
                placeholder="Type a public message..."
                value={message}
                onChange={(e) => {
                  setMessage(e.target.value);
                  setTyping(true);
                }}
                style={{ padding: "8px", width: "70%", marginRight: "10px" }}
              />
              <button type="submit" style={{ padding: "8px 16px" }}>
                Send Public
              </button>
            </form>
          ) : (
            <form onSubmit={handleSendPrivateMessage} style={{ marginTop: "10px" }}>
              <input
                type="text"
                placeholder={`Private message to ${selectedUser}...`}
                value={privateMessage}
                onChange={(e) => setPrivateMessage(e.target.value)}
                style={{ padding: "8px", width: "70%", marginRight: "10px" }}
              />
              <button type="submit" style={{ padding: "8px 16px" }}>
                Send Private
              </button>
            </form>
          )}

          {/* Typing Indicators */}
          {typingUsers.length > 0 && activeTab === "public" && (
            <div style={{ color: "#666", fontStyle: "italic", marginTop: "5px" }}>
              {typingUsers.join(", ")} {typingUsers.length === 1 ? "is" : "are"} typing...
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
