function App() {
  const [username, setUsername] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const [isRegistered, setIsRegistered] = useState(false);
  const [privateMessage, setPrivateMessage] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [privateMessages, setPrivateMessages] = useState({});
  const [activeRoom, setActiveRoom] = useState('general');
  const [rooms, setRooms] = useState(['general', 'nairobi', 'mombasa', 'kisumu', 'jambo']);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, privateMessages]);

  useEffect(() => {
    socket.on('chat message', (data) => {
      setMessages(prev => [...prev, data]);
    });

    socket.on('user joined', (userList) => {
      setUsers(userList);
    });

    socket.on('user left', (userList) => {
      setUsers(userList);
    });

    socket.on('private message', (data) => {
      setPrivateMessages(prev => {
        const key = [data.from, data.to].sort().join('-');
        return {
          ...prev,
          [key]: [...(prev[key] || []), data]
        };
      });
    });

    socket.on('room users', (data) => {
      setUsers(data.users);
    });

    return () => {
      socket.off('chat message');
      socket.off('user joined');
      socket.off('user left');
      socket.off('private message');
      socket.off('room users');
    };
  }, []);

  const handleRegister = (e) => {
    e.preventDefault();
    if (username.trim()) {
      socket.emit('register', username);
      setIsRegistered(true);
      // Join general room by default
      socket.emit('join room', { username, room: 'general' });
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && username) {
      const messageData = {
        text: message,
        sender: username,
        timestamp: new Date().toISOString(),
        room: activeRoom
      };
      socket.emit('chat message', messageData);
      setMessage('');
    }
  };

  const handleSendPrivateMessage = (e) => {
    e.preventDefault();
    if (privateMessage.trim() && selectedUser && username) {
      const privateMessageData = {
        to: selectedUser,
        from: username,
        text: privateMessage,
        timestamp: new Date().toISOString()
      };
      socket.emit('private message', privateMessageData);
      setPrivateMessages(prev => {
        const key = [username, selectedUser].sort().join('-');
        return {
          ...prev,
          [key]: [...(prev[key] || []), privateMessageData]
        };
      });
      setPrivateMessage('');
    }
  };

  const startPrivateChat = (user) => {
    setSelectedUser(user);
  };

  const joinRoom = (room) => {
    socket.emit('join room', { username, room });
    setActiveRoom(room);
    setMessages([]); // Clear messages when switching rooms
  };

  // Kenyan greetings and phrases
  const kenyanGreetings = [
    "Habari yako! üëã",
    "Mambo vipi? üòé",
    "Sasa? üèÄ",
    "Poa kama kile! üå¥",
    "Freshi! üçπ"
  ];

  const quickMessages = [
    "Unaendelea aje?",
    "Uko poa?",
    "Tuko wengi hapa!",
    "Kenya yetu hakuna matata!",
    "Usiku mwema! üåô"
  ];

  const sendQuickMessage = (msg) => {
    setMessage(msg);
  };

  if (!isRegistered) {
    return (
      <div className="App">
        <div className="kenya-pattern"></div>
        <div className="container">
          <div className="register-form floating">
            <h2>üéâ Karibu Kenya Chat Connect! üéâ</h2>
            <p style={{marginBottom: '20px', color: '#666'}}>
              Ingia kujumuika na wakenya wengine duniani kote!
            </p>
            <form onSubmit={handleRegister}>
              <input
                type="text"
                placeholder="Weka jina lako la utani..."
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="kenya-input"
              />
              <button type="submit" className="kenya-button" style={{width: '100%', marginTop: '15px'}}>
                üîê Ingia Mtaani
              </button>
            </form>
            <div style={{marginTop: '20px', fontSize: '0.9rem', color: '#888'}}>
              <strong>üí° Fun Fact:</strong> Unaunganisha Wakenya {users.length} kwenye mfumo!
            </div>
          </div>
        </div>
      </div>
    );
  }

  const getPrivateMessagesWithUser = (user) => {
    const key = [username, user].sort().join('-');
    return privateMessages[key] || [];
  };

  return (
    <div className="App">
      <div className="kenya-pattern"></div>

      {/* Header */}
      <header className="app-header">
        <div className="container">
          <h1>üá∞üá™ Kenya Chat Connect üá∞üá™</h1>
          <h2>Kuunganisha Wakenya Duniani Kote</h2>
        </div>
      </header>

      <div className="container">
        <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px'}}>
          <div>
            <h3 style={{color: 'var(--kenya-green)', margin: 0}}>
              Karibu, <strong>{username}</strong>! üëã
            </h3>
            <p style={{color: '#666', margin: 0}}>Uko kwenye chumba: <strong>{activeRoom}</strong></p>
          </div>
          <div className="kenya-button" style={{padding: '10px 20px'}}>
            Online: {users.length}
          </div>
        </div>

        <div className="chat-container">
          {/* Sidebar */}
          <div className="online-users">
            <h3>üèòÔ∏è Vijana wa Mtaani</h3>
            <div className="users-list">
              {users.filter(user => user !== username).map((user, index) => (
                <div 
                  key={index} 
                  className={`user-item ${selectedUser === user ? 'selected' : ''}`}
                  onClick={() => startPrivateChat(user)}
                >
                  üü¢ {user}
                </div>
              ))}
            </div>

            {/* Chat Rooms */}
            <h3 style={{marginTop: '30px', marginBottom: '15px'}}>üè† Majumba ya Mazungumzo</h3>
            <div style={{display: 'flex', flexDirection: 'column', gap: '10px'}}>
              {rooms.map(room => (
                <button
                  key={room}
                  onClick={() => joinRoom(room)}
                  className={`kenya-button ${activeRoom === room ? '' : 'secondary'}`}
                  style={{
                    background: activeRoom === room ? 
                      'linear-gradient(45deg, var(--kenya-red), var(--kenya-black))' : 
                      'linear-gradient(45deg, #666, #999)',
                    padding: '10px 15px',
                    fontSize: '14px'
                  }}
                >
                  {room === 'general' && 'üåç '}
                  {room === 'nairobi' && 'üèôÔ∏è '}
                  {room === 'mombasa' && 'üèñÔ∏è '}
                  {room === 'kisumu' && 'üêü '}
                  {room === 'jambo' && 'üéâ '}
                  {room.charAt(0).toUpperCase() + room.slice(1)}
                </button>
              ))}
            </div>

            {/* Quick Messages */}
            <h3 style={{marginTop: '30px', marginBottom: '15px'}}>üí¨ Ujumbe wa Haraka</h3>
            <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '8px'}}>
              {quickMessages.map((msg, index) => (
                <button
                  key={index}
                  onClick={() => sendQuickMessage(msg)}
                  className="kenya-button"
                  style={{
                    padding: '8px 12px',
                    fontSize: '12px',
                    background: 'linear-gradient(45deg, var(--kenya-gold), var(--kenya-sun))',
                    color: 'var(--kenya-black)'
                  }}
                >
                  {msg}
                </button>
              ))}
            </div>
          </div>

          {/* Main Chat Area */}
          <div className="main-chat">
            {selectedUser && (
              <div className="private-chat-section">
                <div className="private-chat-header">
                  <h3>üí¨ Mazungumzo na {selectedUser}</h3>
                  <button 
                    onClick={() => setSelectedUser(null)}
                    className="close-button"
                  >
                    ‚úï
                  </button>
                </div>

                <div className="private-messages">
                  {getPrivateMessagesWithUser(selectedUser).map((msg, index) => (
                    <div key={index} className={`message ${msg.from === username ? 'own-message' : 'other-message'}`}>
                      <strong>{msg.from}:</strong> {msg.text}
                      <span className="timestamp">{new Date(msg.timestamp).toLocaleTimeString()}</span>
                    </div>
                  ))}
                </div>

                <form onSubmit={handleSendPrivateMessage} className="private-message-form">
                  <input
                    type="text"
                    placeholder={`Andika ujumbe wa siri kwa ${selectedUser}...`}
                    value={privateMessage}
                    onChange={(e) => setPrivateMessage(e.target.value)}
                    className="kenya-input"
                  />
                  <button type="submit" className="kenya-button">
                    üì§ Tuma
                  </button>
                </form>
              </div>
            )}

            {/* Main Chat Messages */}
            <div className="chat-messages">
              {messages.length === 0 ? (
                <div style={{textAlign: 'center', color: '#666', padding: '40px'}}>
                  <h3>üéâ Karibu kwenye {activeRoom}!</h3>
                  <p>Anza mazungumzo na wenzako kwa kuandika ujumbe hapa chini...</p>
                  <div style={{marginTop: '20px', display: 'flex', flexWrap: 'wrap', gap: '10px', justifyContent: 'center'}}>
                    {kenyanGreetings.map((greeting, index) => (
                      <button
                        key={index}
                        onClick={() => sendQuickMessage(greeting)}
                        className="kenya-button"
                        style={{padding: '8px 15px', fontSize: '12px'}}
                      >
                        {greeting}
                      </button>
                    ))}
                  </div>
                </div>
              ) : (
                messages.map((msg, index) => (
                  <div key={index} className={`message ${msg.sender === username ? 'own-message' : 'other-message'}`}>
                    <strong>{msg.sender}:</strong> {msg.text}
                    {msg.timestamp && <span className="timestamp">{new Date(msg.timestamp).toLocaleTimeString()}</span>}
                  </div>
                ))
              )}
              <div ref={messagesEndRef} />
            </div>

            {/* Message Input Form */}
            <form onSubmit={handleSendMessage} className="message-form">
              <input
                type="text"
                placeholder="Andika ujumbe hapa... (Unaweza pia kutuma Picha, Video au Sauti)"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                className="kenya-input"
              />
              <button type="submit" className="kenya-button">
                üì® Tuma
              </button>
            </form>
          </div>
        </div>

        {/* Footer */}
        <div style={{textAlign: 'center', marginTop: '30px', padding: '20px', borderTop: '2px dashed var(--kenya-gold)', color: '#666'}}>
          <p>
            <strong>üá∞üá™ Kenya Chat Connect</strong> - 
            Inakuunganisha na jamii ya Wakenya duniani kote! 
            üéì Shule Project | üíª Built with React & Socket.io
          </p>
          <div style={{display: 'flex', justifyContent: 'center', gap: '15px', marginTop: '10px', fontSize: '1.5rem'}}>
            <span title="Chat">üí¨</span>
            <span title="Friends">üë•</span>
            <span title="Community">üèòÔ∏è</span>
            <span title="Kenya">üá∞üá™</span>
            <span title="Technology">üíª</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
